mapper.delete:
DELETE FROM t_material
WHERE theme_id = #{themeId}
AND material_id = #{materialId}

mapper.update:
UPDATE t_material
SET title = #{title}
  , content = #{content}
WHERE theme_id = #{themeId}
AND material_id = #{materialId}

mapper.select:
SELECT COUNT(theme_id)
FROM t_material
WHERE theme_id = #{themeId}
